mapper.update:
UPDATE product_info
product_name = #{productName,jdbcType=VARCHAR}, district = #{district,jdbcType=VARCHAR}, category_first_id = #{categoryFirstId,jdbcType=INTEGER}, category_second_id = #{categorySecondId,jdbcType=INTEGER}, category_third_id = #{categoryThirdId,jdbcType=INTEGER}, brand_id = #{brandId,jdbcType=INTEGER}, main_image = #{mainImage,jdbcType=VARCHAR}, detail_images = #{detailImages,jdbcType=VARCHAR}, description = #{description,jdbcType=VARCHAR}, specifications = #{specifications,jdbcType=VARCHAR}, status = #{status,jdbcType=TINYINT}, create_pin = #{createPin,jdbcType=VARCHAR}, create_time = #{createTime,jdbcType=TIMESTAMP}, update_pin = #{updatePin,jdbcType=VARCHAR}, update_time = #{updateTime,jdbcType=TIMESTAMP}, is_delete = #{isDelete,jdbcType=TINYINT},
WHERE id = #{id,jdbcType=INTEGER}

mapper.select:
SELECT * FROM product_info WHERE category_first_id = #{categoryId,jdbcType=INTEGER} 
OR category_second_id = #{categoryId,jdbcType=INTEGER} 
OR category_third_id = #{categoryId,jdbcType=INTEGER}
