mapper.update:
UPDATE product_info
product_name = #{productName,jdbcType=VARCHAR}, district = #{district,jdbcType=VARCHAR}, category_first_id = #{categoryFirstId,jdbcType=INTEGER}, category_second_id = #{categorySecondId,jdbcType=INTEGER}, category_third_id = #{categoryThirdId,jdbcType=INTEGER}, brand_id = #{brandId,jdbcType=INTEGER}, main_image = #{mainImage,jdbcType=VARCHAR}, thumbnail_image = #{thumbnailImage,jdbcType=VARCHAR}, listing_image = #{listingImage,jdbcType=VARCHAR}, detail_images = #{detailImages,jdbcType=VARCHAR}, description = #{description,jdbcType=VARCHAR}, specifications = #{specifications,jdbcType=VARCHAR}, status = #{status,jdbcType=TINYINT}, create_pin = #{createPin,jdbcType=VARCHAR}, create_time = #{createTime,jdbcType=TIMESTAMP}, update_pin = #{updatePin,jdbcType=VARCHAR}, update_time = #{updateTime,jdbcType=TIMESTAMP}, is_delete = #{isDelete,jdbcType=TINYINT},
WHERE id = #{id,jdbcType=INTEGER}

mapper.select:
SELECT pi.id, pi.product_name, pi.district, pi.category_first_id, pi.category_second_id, pi.category_third_id, pi.brand_id, pi.main_image, pi.thumbnail_image, pi.listing_image, pi.detail_images, pi.description, pi.specifications, pi.status, pi.create_pin, pi.create_time, pi.update_pin, pi.update_time, pi.is_delete, pp.original_price, pp.selling_price, pp.bulk_purchase_price, ps.available_quantity, ps.sales FROM product_info pi INNER JOIN product_price pp ON pi.id = pp.product_id INNER JOIN product_stock ps ON pi.id = ps.product_id WHERE pi.status = 1 AND pi.is_delete = 0 AND pp.selling_price > 0.00 AND NOW() BETWEEN pp.effective_time AND IFNULL(pp.expiry_time, NOW()) AND pp.is_delete = 0 AND ps.available_quantity > 0 AND ps.is_delete = 0
AND pi.category_third_id = #{thirdCategoryId,jdbcType=INTEGER}
ORDER BY RAND() LIMIT #{pageSize,jdbcType=INTEGER} OFFSET #{pageNum,jdbcType=INTEGER}

